import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('/api/users/login', { email, password });
            setMessage('✅ Login successful!');
            onLogin(res.data.user);
        } catch (err) {
            setMessage(err.response?.data?.message || '❌ Login failed');
        }
    };

    const containerStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(to right, #dbeafe, #ddd6fe)',
        fontFamily: 'Segoe UI, sans-serif',
    };

    const cardStyle = {
        backgroundColor: 'white',
        padding: '40px',
        borderRadius: '16px',
        boxShadow: '0 8px 25px rgba(0,0,0,0.1)',
        width: '100%',
        maxWidth: '400px',
        boxSizing: 'border-box',
        animation: 'fadeIn 0.4s ease-in-out',
    };

    const headingStyle = {
        textAlign: 'center',
        fontSize: '28px',
        fontWeight: 'bold',
        marginBottom: '24px',
        color: '#1f2937',
    };

    const inputStyle = {
        width: '100%',
        padding: '12px 16px',
        marginBottom: '16px',
        borderRadius: '8px',
        border: '1px solid #ccc',
        outline: 'none',
        fontSize: '16px',
        transition: '0.3s',
    };

    const inputFocusStyle = {
        borderColor: '#60a5fa',
        boxShadow: '0 0 0 3px rgba(96, 165, 250, 0.3)',
    };

    const buttonStyle = {
        width: '100%',
        backgroundColor: '#2563eb',
        color: 'white',
        padding: '12px',
        fontSize: '16px',
        fontWeight: 'bold',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        transition: '0.3s',
    };

    const messageStyle = {
        marginTop: '16px',
        textAlign: 'center',
        color: message.includes('success') ? 'green' : 'red',
        fontWeight: '500',
    };

    const linkStyle = {
        textAlign: 'center',
        fontSize: '14px',
        marginTop: '20px',
        color: '#6b7280',
    };

    const [focus, setFocus] = useState({ email: false, password: false });

    return (
        <div style={containerStyle}>
            <div style={cardStyle}>
                <h2 style={headingStyle}>Login</h2>
                <form onSubmit={handleLogin}>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                        onFocus={() => setFocus({ ...focus, email: true })}
                        onBlur={() => setFocus({ ...focus, email: false })}
                        style={{
                            ...inputStyle,
                            ...(focus.email ? inputFocusStyle : {})
                        }}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        onFocus={() => setFocus({ ...focus, password: true })}
                        onBlur={() => setFocus({ ...focus, password: false })}
                        style={{
                            ...inputStyle,
                            ...(focus.password ? inputFocusStyle : {})
                        }}
                        required
                    />
                    <button type="submit" style={buttonStyle}>Login</button>
                </form>
                {message && <p style={messageStyle}>{message}</p>}
                <div style={linkStyle}>
                    Don’t have an account? <a href="/signup" style={{ color: '#2563eb', textDecoration: 'none' }}>Sign up</a>
                </div>
            </div>
        </div>
    );
};

export default Login;
