import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FoodTracker = () => {
    const [foodItems, setFoodItems] = useState([]);
    const [foodName, setFoodName] = useState('');
    const [calories, setCalories] = useState('');
    const [intakeDate, setIntakeDate] = useState(new Date().toISOString().split('T')[0]);

    useEffect(() => {
        fetchFoodItems();
    }, []);

    const fetchFoodItems = async () => {
        try {
            const response = await axios.get('/api/food');
            setFoodItems(response.data);
        } catch (error) {
            console.error('Error fetching food items:', error);
        }
    };

    const handleAddFood = async (e) => {
        e.preventDefault();
        try {
            const newFood = { name: foodName, calories: parseInt(calories), date: intakeDate };
            await axios.post('/api/food', newFood);
            setFoodName('');
            setCalories('');
            setIntakeDate(new Date().toISOString().split('T')[0]);
            fetchFoodItems();
        } catch (error) {
            console.error('Error adding food item:', error);
        }
    };

    return (
        <div>
            <h2>Food Tracker</h2>
            <form onSubmit={handleAddFood}>
                <input
                    type="text"
                    placeholder="Food Name"
                    value={foodName}
                    onChange={(e) => setFoodName(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Calories"
                    value={calories}
                    onChange={(e) => setCalories(e.target.value)}
                    required
                />
                <input
                    type="date"
                    value={intakeDate}
                    onChange={(e) => setIntakeDate(e.target.value)}
                    required
                />
                <button type="submit">Add Food</button>
            </form>
            <h3>Logged Food Items</h3>
            <ul>
                {foodItems.map((item) => (
                    <li key={item._id}>
                        {item.name} - {item.calories} calories on {new Date(item.date).toLocaleDateString()}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FoodTracker;
