import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [otp, setOtp] = useState('');
    const [step, setStep] = useState(1);
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleRequestOtp = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');
        try {
            await axios.post('/api/users/request-otp', { email, password });
            setStep(2);
            setMessage('OTP sent to your email.');
        } catch (err) {
            setMessage(err.response?.data?.message || 'Error requesting OTP');
        }
        setLoading(false);
    };

    const handleVerifyOtp = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');
        try {
            await axios.post('/api/users/verify-otp', { email, otp });
            setMessage('Signup successful! You can now log in.');
            setStep(3);
        } catch (err) {
            setMessage(err.response?.data?.message || 'Error verifying OTP');
        }
        setLoading(false);
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
            <div className="card" style={{ maxWidth: 400, margin: '0 auto', boxShadow: 'var(--shadow)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: 8 }}>Sign Up</h2>
                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
                    <div style={{ display: 'flex', gap: 8 }}>
                        <div style={{ width: 18, height: 18, borderRadius: '50%', background: step >= 1 ? 'var(--color-primary)' : '#e5e7eb', transition: 'background 0.3s' }}></div>
                        <div style={{ width: 40, height: 2, background: step >= 2 ? 'var(--color-primary)' : '#e5e7eb', alignSelf: 'center', transition: 'background 0.3s' }}></div>
                        <div style={{ width: 18, height: 18, borderRadius: '50%', background: step >= 2 ? 'var(--color-primary)' : '#e5e7eb', transition: 'background 0.3s' }}></div>
                    </div>
                </div>
                {step === 1 && (
                    <form onSubmit={handleRequestOtp} style={{ display: 'flex', flexDirection: 'column', gap: 14 }}>
                        <input
                            type="email"
                            placeholder="Email"
                            value={email}
                            onChange={e => setEmail(e.target.value)}
                            required
                            style={{ padding: 12, borderRadius: 8, border: '1px solid #e5e7eb', fontSize: 16 }}
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                            required
                            style={{ padding: 12, borderRadius: 8, border: '1px solid #e5e7eb', fontSize: 16 }}
                        />
                        <button type="submit" className="button" disabled={loading} style={{ marginTop: 8 }}>
                            {loading ? 'Requesting OTP...' : 'Request OTP'}
                        </button>
                    </form>
                )}
                {step === 2 && (
                    <form onSubmit={handleVerifyOtp} style={{ display: 'flex', flexDirection: 'column', gap: 14 }}>
                        <input
                            type="text"
                            placeholder="Enter OTP"
                            value={otp}
                            onChange={e => setOtp(e.target.value)}
                            required
                            style={{ padding: 12, borderRadius: 8, border: '1px solid #e5e7eb', fontSize: 16, letterSpacing: 4 }}
                        />
                        <button type="submit" className="button" disabled={loading} style={{ marginTop: 8 }}>
                            {loading ? 'Verifying...' : 'Verify OTP'}
                        </button>
                    </form>
                )}
                {step === 3 && (
                    <div style={{ textAlign: 'center', marginTop: 24 }}>
                        <div style={{ fontSize: '2.2rem', color: 'var(--color-accent)', marginBottom: 8 }}>🎉</div>
                        <div style={{ fontWeight: 600, marginBottom: 8 }}>Signup successful!</div>
                        <a href="/login" className="button" style={{ display: 'inline-block', marginTop: 12 }}>Go to Login</a>
                    </div>
                )}
                {message && <div style={{ marginTop: 18, color: message.includes('success') ? 'var(--color-accent)' : 'crimson', textAlign: 'center', fontWeight: 500 }}>{message}</div>}
            </div>
        </div>
    );
};

export default Signup;
