import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StreakTracker = () => {
    const [streak, setStreak] = useState(0);
    const [lastLogin, setLastLogin] = useState(null);

    useEffect(() => {
        const fetchStreakData = async () => {
            try {
                const response = await axios.get('/api/streak'); // Adjust the endpoint as necessary
                setStreak(response.data.streak);
                setLastLogin(response.data.lastLogin);
            } catch (error) {
                console.error('Error fetching streak data:', error);
            }
        };

        fetchStreakData();
    }, []);

    return (
        <div className="streak-tracker">
            <h2>Your Daily Login Streak</h2>
            <p>Current Streak: {streak} days</p>
            {lastLogin && <p>Last Login: {new Date(lastLogin).toLocaleDateString()}</p>}
        </div>
    );
};

export default StreakTracker;
