import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WalkTracker = () => {
    const [distance, setDistance] = useState(0);
    const [walks, setWalks] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchWalks();
    }, []);

    const fetchWalks = async () => {
        try {
            const response = await axios.get('/api/walks'); // Adjust the endpoint as necessary
            setWalks(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching walks:', error);
            setLoading(false);
        }
    };

    const handleAddWalk = async () => {
        if (distance <= 0) return;

        try {
            const response = await axios.post('/api/walks', { distance });
            setWalks([...walks, response.data]);
            setDistance(0);
        } catch (error) {
            console.error('Error adding walk:', error);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="walk-tracker">
            <h2>Walk Tracker</h2>
            <input
                type="number"
                value={distance}
                onChange={(e) => setDistance(e.target.value)}
                placeholder="Distance in km"
            />
            <button onClick={handleAddWalk}>Add Walk</button>
            <h3>Your Walks</h3>
            <ul>
                {walks.map((walk, index) => (
                    <li key={index}>{walk.distance} km</li>
                ))}
            </ul>
        </div>
    );
};

export default WalkTracker;
