import React, { useEffect, useState } from 'react';
import axios from 'axios';
import WorkoutTracker from './WorkoutTracker';
import FoodTracker from './FoodTracker';
import CalorieTracker from './CalorieTracker';
import StreakTracker from './StreakTracker';
import WalkTracker from './WalkTracker';

const Dashboard = ({ user }) => {
    const [userData, setUserData] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`/api/users/${user._id}`);
                setUserData(response.data);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        if (user && user._id) {
            fetchUserData();
        }
    }, [user]);

    if (!userData) {
        return <div>Loading...</div>;
    }

    return (
        <div className="dashboard">
            <h1>Welcome, {userData.email}</h1>
            <CalorieTracker caloriesConsumed={userData.calories} />
            <WorkoutTracker workouts={userData.workouts} />
            <FoodTracker foodIntake={userData.foodIntake} />
            <StreakTracker streak={userData.streak} />
            <WalkTracker distanceWalked={userData.distanceWalked} />
        </div>
    );
};

export default Dashboard;
