import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CalorieTracker = () => {
    const [caloriesConsumed, setCaloriesConsumed] = useState(0);
    const [caloriesBurned, setCaloriesBurned] = useState(0);

    useEffect(() => {
        const fetchCalorieData = async () => {
            try {
                const response = await axios.get('/api/user/calories');
                setCaloriesConsumed(response.data.caloriesConsumed);
                setCaloriesBurned(response.data.caloriesBurned);
            } catch (error) {
                console.error('Error fetching calorie data:', error);
            }
        };

        fetchCalorieData();
    }, []);

    return (
        <div className="calorie-tracker">
            <h2>Calorie Tracker</h2>
            <div>
                <p>Calories Consumed: {caloriesConsumed}</p>
                <p>Calories Burned: {caloriesBurned}</p>
                <p>Net Calories: {caloriesConsumed - caloriesBurned}</p>
            </div>
        </div>
    );
};

export default CalorieTracker;
