import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [focus, setFocus] = useState({ email: false, password: false });
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);

    const emailRef = useRef(null);

    useEffect(() => {
        emailRef.current?.focus(); // Autofocus on first load
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');
        try {
            const res = await axios.post('/api/users/login', { email, password });
            setMessage('✅ Login successful!');
            onLogin(res.data.user);
        } catch (err) {
            setMessage(err.response?.data?.message || '❌ Login failed');
        }
        setLoading(false);
    };

    const containerStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(to right, #dbeafe, #ddd6fe)',
        fontFamily: 'Segoe UI, sans-serif',
    };

    const cardStyle = {
        backgroundColor: 'white',
        padding: '40px 32px',
        borderRadius: '16px',
        boxShadow: '0 8px 25px rgba(0,0,0,0.1)',
        width: '100%',
        maxWidth: '400px',
        boxSizing: 'border-box',
        transform: 'scale(0.95)',
        opacity: 0,
        animation: 'fadeScaleIn 0.5s ease forwards',
    };

    const headingStyle = {
        textAlign: 'center',
        fontSize: '28px',
        fontWeight: 'bold',
        marginBottom: '24px',
        color: '#1f2937',
    };

    const inputStyle = {
        width: '100%',
        padding: '12px 16px',
        marginBottom: '16px',
        borderRadius: '8px',
        border: '1px solid #ccc',
        outline: 'none',
        fontSize: '16px',
        boxSizing: 'border-box',
        transition: '0.3s',
    };

    const inputFocusStyle = {
        borderColor: '#60a5fa',
        boxShadow: '0 0 0 3px rgba(96, 165, 250, 0.3)',
    };

    const buttonStyle = {
        width: '100%',
        backgroundColor: '#2563eb',
        color: 'white',
        padding: '12px',
        fontSize: '16px',
        fontWeight: 'bold',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        transition: '0.3s',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    };

    const spinnerStyle = {
        width: '18px',
        height: '18px',
        border: '2px solid white',
        borderTop: '2px solid transparent',
        borderRadius: '50%',
        animation: 'spin 1s linear infinite',
        marginRight: '8px',
    };

    const toggleStyle = {
        position: 'absolute',
        right: '16px',
        top: 'calc(50% - 10px)',
        cursor: 'pointer',
        color: '#6b7280',
        fontSize: '14px',
    };

    const passwordContainerStyle = {
        position: 'relative',
    };

    const messageStyle = {
        marginTop: '16px',
        textAlign: 'center',
        color: message.includes('success') ? 'green' : 'red',
        fontWeight: '500',
    };

    const linkStyle = {
        textAlign: 'center',
        fontSize: '14px',
        marginTop: '20px',
        color: '#6b7280',
    };

    return (
        <>
            <style>
                {`
                    @keyframes spin {
                        to { transform: rotate(360deg); }
                    }
                    @keyframes fadeScaleIn {
                        0% { opacity: 0; transform: scale(0.95); }
                        100% { opacity: 1; transform: scale(1); }
                    }
                `}
            </style>
            <div style={containerStyle}>
                <div style={cardStyle}>
                    <h2 style={headingStyle}>Login</h2>
                    <form onSubmit={handleLogin}>
                        <input
                            ref={emailRef}
                            type="email"
                            placeholder="Email"
                            value={email}
                            onChange={e => setEmail(e.target.value)}
                            onFocus={() => setFocus({ ...focus, email: true })}
                            onBlur={() => setFocus({ ...focus, email: false })}
                            style={{
                                ...inputStyle,
                                ...(focus.email ? inputFocusStyle : {})
                            }}
                            required
                        />
                        <div style={passwordContainerStyle}>
                            <input
                                type={showPassword ? 'text' : 'password'}
                                placeholder="Password"
                                value={password}
                                onChange={e => setPassword(e.target.value)}
                                onFocus={() => setFocus({ ...focus, password: true })}
                                onBlur={() => setFocus({ ...focus, password: false })}
                                style={{
                                    ...inputStyle,
                                    ...(focus.password ? inputFocusStyle : {})
                                }}
                                required
                            />
                            <span
                                onClick={() => setShowPassword(prev => !prev)}
                                style={toggleStyle}
                            >
                                {showPassword ? 'Hide' : 'Show'}
                            </span>
                        </div>
                        <button type="submit" style={buttonStyle} disabled={loading}>
                            {loading && <span style={spinnerStyle}></span>}
                            {loading ? 'Logging in...' : 'Login'}
                        </button>
                    </form>
                    {message && <p style={messageStyle}>{message}</p>}
                    <div style={linkStyle}>
                        Don’t have an account? <a href="/signup" style={{ color: '#2563eb', textDecoration: 'none' }}>Sign up</a>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Login;
