import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [focus, setFocus] = useState({ email: false, password: false });
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);

    const emailRef = useRef(null);

    useEffect(() => {
        emailRef.current?.focus(); // Autofocus on first load
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');
        try {
            const res = await axios.post('/api/users/login', { email, password });
            setMessage('‚úÖ Login successful!');
            onLogin(res.data.user);
        } catch (err) {
            setMessage(err.response?.data?.message || '‚ùå Login failed');
        }
        setLoading(false);
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
            <div className="card" style={{ maxWidth: 400, width: '100%', boxShadow: 'var(--shadow)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: 8 }}>Login</h2>
                <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: 14 }}>
                    <input
                        ref={emailRef}
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                        onFocus={() => setFocus({ ...focus, email: true })}
                        onBlur={() => setFocus({ ...focus, email: false })}
                        style={{ padding: 12, borderRadius: 8, border: focus.email ? '2px solid var(--color-primary)' : '1px solid #e5e7eb', fontSize: 16, transition: 'border 0.2s' }}
                        required
                    />
                    <div style={{ position: 'relative' }}>
                        <input
                            type={showPassword ? 'text' : 'password'}
                            placeholder="Password"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                            onFocus={() => setFocus({ ...focus, password: true })}
                            onBlur={() => setFocus({ ...focus, password: false })}
                            style={{ padding: 12, borderRadius: 8, border: focus.password ? '2px solid var(--color-primary)' : '1px solid #e5e7eb', fontSize: 16, width: '100%', transition: 'border 0.2s' }}
                            required
                        />
                        <span
                            onClick={() => setShowPassword(prev => !prev)}
                            style={{ position: 'absolute', right: 16, top: '50%', transform: 'translateY(-50%)', cursor: 'pointer', color: '#6b7280', fontSize: 18 }}
                            title={showPassword ? "Hide password" : "Show password"}
                        >
                            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                        </span>
                    </div>
                    <button type="submit" className="button" disabled={loading} style={{ marginTop: 8 }}>
                        {loading ? 'Logging in...' : 'Login'}
                    </button>
                </form>
                {message && <div style={{ marginTop: 18, color: message.includes('success') || message.includes('‚úÖ') ? 'var(--color-accent)' : 'crimson', textAlign: 'center', fontWeight: 500 }}>{message}</div>}
                <div style={{ textAlign: 'center', fontSize: 14, marginTop: 20, color: 'var(--color-muted)' }}>
                    Don't have an account? <a href="/signup" style={{ color: 'var(--color-primary)', textDecoration: 'none', fontWeight: 500 }}>Sign up</a>
                </div>
            </div>
        </div>
    );
};

export default Login;
