import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect, Link } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import Signup from './components/Signup';
import Login from './components/Login';
import './styles/main.css';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [user, setUser] = useState(null);

  return (
    <Router>
      <header>
        <span style={{ fontWeight: 700, fontSize: '1.5rem', letterSpacing: '1px' }}>
          <span role="img" aria-label="dumbbell">üèãÔ∏è‚Äç‚ôÇÔ∏è</span> FitTrack Pro
        </span>
      </header>

      <nav>
        <div>
          <Link to="/">Dashboard</Link>
        </div>
        <div>
          {!user ? (
            <>
              <Link to="/login">Login</Link>
              <Link to="/signup">Sign Up</Link>
            </>
          ) : (
            <span style={{ color: 'var(--color-muted)', fontWeight: 500 }}>
              Welcome, {user.email}
            </span>
          )}
        </div>
      </nav>

      <div style={{ minHeight: 'calc(100vh - 160px)' }}>
        <Switch>
          <Route path="/signup" component={Signup} />
          <Route
            path="/login"
            render={() =>
              user ? <Redirect to="/" /> : <Login onLogin={setUser} />
            }
          />
          <Route
            exact
            path="/"
            render={() =>
              user ? <Dashboard user={user} /> : <Redirect to="/login" />
            }
          />
        </Switch>
      </div>

      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />

      <footer className="footer">
        &copy; {new Date().getFullYear()} FitTrack Pro &mdash; Your Fitness Journey, Upgraded
      </footer>
    </Router>
  );
};

export default App;
