import React, { useEffect, useState } from 'react';
import axios from 'axios';

const typeOptions = [
  { value: 'workout', label: 'Workout (minutes)' },
  { value: 'walk', label: 'Walk (km)' },
  { value: 'food', label: 'Calories' },
  { value: 'custom', label: 'Custom' }
];

const GoalTracker = ({ user }) => {
  const [goals, setGoals] = useState([]);
  const [form, setForm] = useState({ type: 'workout', target: '', unit: '', endDate: '', description: '' });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  // fetchGoals is now outside useEffect
  const fetchGoals = async (isMounted = true) => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get(`/api/goals/${user._id}`);
      if (isMounted) setGoals(res.data);
    } catch (err) {
      if (isMounted) setError('Failed to fetch goals.');
    }
    if (isMounted) setLoading(false);
  };

  useEffect(() => {
    let isMounted = true;
    if (user && user._id) fetchGoals(isMounted);
    return () => { isMounted = false; };
    // eslint-disable-next-line
  }, [user]);

  const handleFormChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddGoal = async e => {
    e.preventDefault();
    setMessage('');
    setError('');
    try {
      const payload = {
        ...form,
        userId: user._id,
        target: Number(form.target),
        unit: form.unit || (form.type === 'workout' ? 'min' : form.type === 'walk' ? 'km' : form.type === 'food' ? 'kcal' : ''),
      };
      await axios.post('/api/goals', payload);
      setForm({ type: 'workout', target: '', unit: '', endDate: '', description: '' });
      setMessage('Goal added!');
      fetchGoals();
    } catch (err) {
      setError('Failed to add goal.');
    }
  };

  const handleDelete = async goalId => {
    setError('');
    try {
      await axios.delete(`/api/goals/${goalId}`);
      fetchGoals();
    } catch (err) {
      setError('Failed to delete goal.');
    }
  };

  const handleMarkComplete = async goal => {
    setError('');
    try {
      await axios.patch(`/api/goals/${goal._id}`, { status: 'completed' });
      fetchGoals();
    } catch (err) {
      setError('Failed to update goal.');
    }
  };

  if (loading) return <div style={{ textAlign: 'center', marginTop: 40 }}>Loading goals...</div>;

  return (
    <div className="container" style={{ maxWidth: 700, margin: '40px auto' }}>
      <h2 style={{ marginBottom: 24 }}>Goals</h2>
      <div className="card" style={{ marginBottom: 32 }}>
        <h3>Add New Goal</h3>
        <form onSubmit={handleAddGoal} style={{ display: 'flex', gap: 12, flexWrap: 'wrap', alignItems: 'center' }}>
          <select name="type" value={form.type} onChange={handleFormChange} style={{ padding: 8, borderRadius: 8, border: '1px solid #e5e7eb' }}>
            {typeOptions.map(opt => <option key={opt.value} value={opt.value}>{opt.label}</option>)}
          </select>
          <input name="target" type="number" placeholder="Target" value={form.target} onChange={handleFormChange} required style={{ padding: 8, borderRadius: 8, border: '1px solid #e5e7eb', width: 90 }} />
          {form.type === 'custom' && (
            <input name="unit" placeholder="Unit" value={form.unit} onChange={handleFormChange} style={{ padding: 8, borderRadius: 8, border: '1px solid #e5e7eb', width: 80 }} />
          )}
          <input name="endDate" type="date" value={form.endDate} onChange={handleFormChange} style={{ padding: 8, borderRadius: 8, border: '1px solid #e5e7eb' }} />
          <input name="description" placeholder="Description (optional)" value={form.description} onChange={handleFormChange} style={{ padding: 8, borderRadius: 8, border: '1px solid #e5e7eb', flex: 1 }} />
          <button type="submit" className="button">Add Goal</button>
        </form>
        {message && <div style={{ color: 'var(--color-accent)', marginTop: 8 }}>{message}</div>}
        {error && <div style={{ color: 'crimson', marginTop: 8 }}>{error}</div>}
      </div>
      <div className="card">
        <h3>Active Goals</h3>
        {goals.length === 0 && <div style={{ color: 'var(--color-muted)' }}>No goals set yet.</div>}
        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
          {goals.map(goal => (
            <li key={goal._id} style={{ marginBottom: 18, borderBottom: '1px solid #eee', paddingBottom: 12 }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div>
                  <b>{goal.type.charAt(0).toUpperCase() + goal.type.slice(1)}</b> Goal: {goal.target} {goal.unit}
                  {goal.description && <span style={{ color: 'var(--color-muted)', marginLeft: 8 }}>({goal.description})</span>}
                  {goal.endDate && <span style={{ color: 'var(--color-primary)', marginLeft: 8 }}>by {new Date(goal.endDate).toLocaleDateString()}</span>}
                </div>
                <div>
                  {goal.status === 'active' && <button className="button" style={{ marginRight: 8, background: '#22c55e', color: '#fff' }} onClick={() => handleMarkComplete(goal)}>Mark Complete</button>}
                  <button className="button" style={{ background: '#e5e7eb', color: '#222' }} onClick={() => handleDelete(goal._id)}>Delete</button>
                </div>
              </div>
              <div style={{ marginTop: 8, marginBottom: 4 }}>
                <div style={{ height: 8, background: '#e5e7eb', borderRadius: 8 }}>
                  <div style={{ width: `${Math.min((goal.current / goal.target) * 100, 100)}%`, background: 'var(--color-primary)', height: '100%', borderRadius: 8, transition: 'width 0.4s' }}></div>
                </div>
                <span style={{ fontSize: 13, color: 'var(--color-muted)' }}>Progress: {goal.current} / {goal.target} {goal.unit}</span>
                {goal.status === 'completed' && <span style={{ color: '#22c55e', marginLeft: 8, fontWeight: 600 }}>Completed</span>}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default GoalTracker;
